import React from 'react';
import logo from './logo.svg';
import './App.css';


class TodoApp extends React.Component {
  constructor(props) {
    super(props);
    this.state = { items: [], text: '' };
    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);

  }

  render() {
    return (
      <div>
        <h3>TODO</h3>
        <TodoList items={this.state.items} />
        <form onSubmit={this.handleSubmit}>
          <input
            onChange={this.handleChange}
            value={this.state.text}
          />
          <button>
            Add new task: {this.state.items.length + 1}
          </button>
        </form>
      </div>
    );
  }

  handleChange(e) {
    this.setState({ text: e.target.value });
  }

  handleSubmit(e) {
    e.preventDefault();
    if (!this.state.text.length) {
      return;
    }
    const newItem = {
      text: this.state.text,
      id: Date.now()
    };
    this.setState(prevState => ({
      items: prevState.items.concat(newItem),
      text: ''
    }));
  }
}

class TodoList extends React.Component {
  renderTodo(text) {
    return(
      <Todo content={text}/>
    );
  }
  render() {
    return (
      <ol>
        {this.props.items.map(item => (
          <li key={item.id}> {this.renderTodo(item.text)} <button>X</button></li>
        ))}
      </ol>
    );
  }
}

class Todo extends React.Component {
  constructor(props) {
    super(props)
    this.state = {
    done: false,
    style: "text-decoration:none",
    }
  }
  handleDone() {
    this.setState({
      done: true,
      style:"text-decoration:line-through;"
    })
  }
  render() {
    return(
      <button disabled={this.state.done} style={this.state.style} onClick={()=>this.handleDone()}>{this.props.content}</button>
    );
  }


}

