import React from 'react';
import logo from './logo.svg';
import './App.css';


class TodoList extends React.Component {
  constructor(props) {
    super(props);
    this.state = { items: [], text: '' };
    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
    this.handleDelete = this.handleDelete.bind(this)
  }
  renderTodo(text) {
    return(
      <Todo content={text}/>
    );
  }
  render() {
    return (
      <div>
        <h3>&emsp;&emsp;&emsp;Todo List</h3>
        <ol>
        {this.state.items.map(item => (
          <li key={item.id}><input type={"checkbox"}>{this.renderTodo(item.text)}</input> &emsp; <button id={item.id} onClick={this.handleDelete}>delete</button></li>
        ))}
        </ol>
        <form onSubmit={this.handleSubmit}>
          <input
            onChange={this.handleChange}
            value={this.state.text}
          />
          <button>
            Add new task: {this.state.items.length + 1}
          </button>
        </form>
      </div>
    );
  }

  handleDelete(e) {
    var id = e.target.getAttribute('id');
    this.setState(prevState => ({
      items: prevState.items.filter(item => item.id != id)
    }))
  }

  handleChange(e) {
    this.setState({ text: e.target.value });
  }

  handleSubmit(e) {
    e.preventDefault();
    if (!this.state.text.length) {
      return;
    }
    const newItem = {
      text: this.state.text,
      id: Date.now()
    };
    this.setState(prevState => ({
      items: prevState.items.concat(newItem),
      text: ''
    }));
  }
}

class Todo extends React.Component {
  constructor(props) {
    super(props)
    this.state = {
    done: false,
    //style: 'text-decoration:none'
    }
  }
  handleDone() {
    this.setState({
      done: true,
      //style: 'text-decoration:line-through'
    })
  }
  render() {
    return(
      <button disabled={this.state.done}  onClick={()=>this.handleDone()}>{this.props.content}</button>
    );
  }
}

//ReactDOM.render(<TodoList />, mountNode);

export default TodoList;
